import './Product.css';
import React, {useState} from "react";
import {useSelector, useDispatch} from "react-redux";

import {getProductDetails} from "../actions/productActions";
import {addToCart} from "../actions/cartActions";

import {useParams, useHistory} from "react-router-dom";

const Product = ({history}) => {

    const [qty, setQty] = useState(1)
    const dispatch = useDispatch()

    const productDetails = useSelector((state) => state.getProductDetails);
    const { loading, error, product } = productDetails;

    React.useEffect(() => {
        if (product && useParams.params.id !== product._id) {
            dispatch(getProductDetails(useParams.params.id));
        }
    }, [dispatch, useParams, product]);

    return <div className={'productscreen'}>
        <div className={'productscreen-left'}>
            <div className={'left-image'}>
                {/*<img src={imageUrl} alt={name}/>*/}
            </div>

            <div className={'left-info'}>
                {/*<p className={'left-name'}>{name}</p>*/}
                {/*<p>{price}</p>*/}
                {/*<p>{description}</p>*/}
            </div>
        </div>
        <div className={'productscreen-right'}>
            <div className={'right-info'}>
                <p>
                    {/*Price: <span>{price}</span>*/}
                </p>
                <p>
                    Status: <span>In Stock</span>
                </p>
                <p>
                    Qty
                    <select>
                        <option value="1">1</option>
                        <option value="2">2</option>
                        <option value="3">3</option>
                        <option value="4">4</option>
                    </select>
                </p>
                <p>
                    <button type={'button'}>Add To Cart</button>
                </p>
            </div>
        </div>
    </div>
}

export default Product
