import './SideDrawer.css'

const SideDrawer = ({show}) => {
    const sideDrawerClass = ['side_drawer']

    if (show) {
        sideDrawerClass.push('show')
    }
    return <div className={sideDrawerClass.join(' ')}></div>
}

export default SideDrawer
